import React, { useState, useEffect, useContext } from 'react';
import io from 'socket.io-client';
import UserContext from '../UserContext';
import './Chat.css';  // Importing a CSS file to style your components

let socket;

function Chat() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [youtubeUrl, setYoutubeUrl] = useState('');  // State to hold YouTube URL
  const { user } = useContext(UserContext);

  useEffect(() => {
    // ... (same as before)
  }, [user]);

  const sendMessage = () => {
    // ... (same as before)
  };

  const handleKeyPress = (e) => {
    // ... (same as before)
  }

  return (
    <div className="chat-container">
      <div className="video-player">
        {user && <input 
          type="text" 
          placeholder="Paste YouTube URL here"
          value={youtubeUrl}
          onChange={(e) => setYoutubeUrl(e.target.value)}
        />}
        {youtubeUrl && <iframe 
          width="100%" 
          height="100%" 
          src={`https://www.youtube.com/embed/${youtubeUrl.split('v=')[1]}`}
          title="YouTube video player" 
          frameBorder="0" 
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
          allowFullScreen
        />}
      </div>
      <div className="chat-section">
        <h2>Chat</h2>
        <div className="chat-messages">
          {chat.map((msg, index) => (
            <div key={index} className="chat-message">
              <b>{msg.username}</b>
              <p>{msg.message}</p>
            </div>
          ))}
        </div>
        <input
          type="text"
          className="chat-input"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button onClick={sendMessage}>Send</button>
        {!user && <p style={{ color: 'red' }}>Please log in to join the chat.</p>}
      </div>
    </div>
  );
}

export default Chat;
