import React, { useState, useEffect, useContext } from 'react';
import io from 'socket.io-client';
import UserContext from '../UserContext';
import './Chat.css'; // Import your CSS file

let socket;

function Chat() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const { user } = useContext(UserContext);

  useEffect(() => {
    socket = io('http://localhost:5000');

    socket.on('previous_messages', (messages) => {
      console.log('Received previous messages', messages);
      setChat(messages);
    });

    socket.on('receive_message', (message) => {
      console.log('Received a new message', message);
      setChat((oldChat) => [...oldChat, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const sendMessage = () => {
    if (message && user) {
      const newMessage = { username: user.username, message };
      console.log('Sending a new message', newMessage);
      socket.emit('send_message', newMessage);
      setMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="chat-container">
      <h2>Chat</h2>
      <div className="chat-messages">
        {chat.map((msg, index) => (
          <div key={index} className="chat-message">
            <b>{msg.username}</b>
            <p>{msg.message}</p>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyPress={handleKeyPress}
        className="chat-input"
      />
      <button onClick={sendMessage} className="chat-send-button">Send</button>
      {!user && <p style={{ color: 'red' }}>Please log in to join the chat.</p>}
    </div>
  );
}

export default Chat;
