import React, { useState } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import Chat from './components/Chat'; 
import UserContext from './UserContext';  // Import UserContext

function App() {
  const [user, setUser] = useState(null);  // Create a state variable for user data

  return (
    <UserContext.Provider value={{ user, setUser }}>  {/* Provide the user data and setter function via context */}
      <div>
        <Register />
        <Login />
        <Chat /> 
      </div>
    </UserContext.Provider>
  );
}

export default App;
