import React, { useState, useEffect, useContext } from 'react';
import io from 'socket.io-client';
import UserContext from '../UserContext';

let socket;

function Chat() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const { user } = useContext(UserContext);

  useEffect(() => {
    socket = io('http://localhost:5000');

    socket.on('previous_messages', (messages) => {
      console.log('Received previous messages', messages);
      setChat(messages);
    });

    socket.on('receive_message', (message) => {
      console.log('Received a new message', message);
      setChat((oldChat) => [...oldChat, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const sendMessage = () => {
    if (message && user) {
      const newMessage = { username: user.username, message };
      console.log('Sending a new message', newMessage);
      socket.emit('send_message', newMessage);
      setMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div>
      <h2>Chat</h2>
      <div style={{
        border: '1px solid #ccc',
        padding: '10px',
        height: '300px',
        overflowY: 'scroll',
      }}>
        {chat.map((msg, index) => (
          <p key={index}><b>{msg.username}</b>: {msg.message}</p>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyPress={handleKeyPress}
      />
      <button onClick={sendMessage}>Send</button>
      {!user && <p style={{ color: 'red' }}>Please log in to join the chat.</p>}
    </div>
  );
}

export default Chat;
