import React, { useState, useContext } from 'react';
import axios from 'axios';
import UserContext from '../UserContext'; 
import ThemeContext from '../ThemeContext'; 

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  
  const { user, setUser } = useContext(UserContext);
  const { theme } = useContext(ThemeContext);  

  const login = async () => {
    try {
      const res = await axios.post('http://localhost:5000/login', { username, password });
      setMessage(res.data.message);
      
      if (res.data.message === "Logged in successfully") {
        const userData = { username };
        setUser(userData);  
        localStorage.setItem('user', JSON.stringify(userData));  
      }
    } catch (error) {
      setMessage(error.response?.data?.message || "An error occurred");  
    }
  };
 
  const textStyles = {
    color: theme === 'day' ? '#000' : '#c0c0c0',
  };

  const inputStyles = {
    color: theme === 'day' ? '#000' : '#c0c0c0',
    backgroundColor: theme === 'day' ? '#fff' : '#2f2f2f',
    transition: 'all 0.5s',
    marginBottom: '8px', 
    padding: '8px', 
    borderRadius: '4px', 
    border: '1px solid #ccc'
  };

  const buttonStyles = {
    color: theme === 'day' ? '#000' : '#c0c0c0',
    backgroundColor: theme === 'day' ? '#fff' : '#2f2f2f',
    transition: 'all 0.5s',
    padding: '10px 20px', 
    borderRadius: '4px', 
    border: '1px solid #ccc', 
    cursor: 'pointer'
  };

  const headerStyles = {
    background: 'linear-gradient(to right, #b3cde0, #6497b1)',
    padding: '10px',
    borderRadius: '8px',
    color: '#fff', 
    marginBottom: '16px',
  };

  return (
    <>
      <h4 style={headerStyles}>Login to Chat and Post Videos</h4>
      {user ? (
        <p style={textStyles}>{message}</p>
      ) : (
        <>
          <input 
            type="text" 
            placeholder="Username" 
            value={username}
            onChange={(e) => setUsername(e.target.value)} 
            style={inputStyles}
          />
          <input 
            type="password" 
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} 
            style={inputStyles}
          />
          <button onClick={login} style={buttonStyles}>Login</button>
          <p style={{ ...textStyles, marginTop: '16px' }}>{message}</p>
        </>
      )}
    </>
  );
}

export default Login;
